mapper.select:
select * from customer where 1=1 

	and sales = #{sales}


	and department = #{department}


	 and DATE_FORMAT(createTime, '%Y-%m-%d') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d') 


	 and DATE_FORMAT(createTime, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

mapper.insert:
insert into customer ( name, telephone, sales, department, sex, 
source, houseType, houseState, personnelAttr, customerAttr,
customerStage, SPcustomer, houseDemand, lossStatus, weiXin,
UserID, releWeixin, createName, createTime,  updateName, updateTime
) values (
#{name}, #{telephone}, #{sales}, #{department}, #{sex}, 
#{source}, #{houseType}, #{houseState}, #{personnelAttr}, #{customerAttr},
#{customerStage}, #{SPcustomer}, #{houseDemand}, #{lossStatus}, #{weiXin},
#{UserID}, #{releWeixin}, #{createName}, #{createTime,jdbcType=DATE}, #{updateName}, #{updateTime,jdbcType=DATE}
)
