mapper.select:
# 方案ID查询
select *
from schemehistory where id = #{id};

mapper.insert:
# 方案新增
insert into schemehistory (projectId, productId, number, installNumber,
           debugNumber, notInstalled, unregulated, notSent, itemId) value (
   #{projectId}, #{productId}, #{number}, #{installNumber}, #{debugNumber},
   #{notInstalled}, #{unregulated}, #{notSent}, #{itemId}
)

        # 方案新增
        update schemehistory
        set number = #{number},
            notSent  = #{notSent}
#             notInstalled  = #{notInstalled},
#             unregulated   = #{unregulated}
        where itemId = #{itemId}
