mapper.insert:
insert into openMeasure (
    projectId, area, motorModel, trackType, installMethod, number,
    openMethod, powerPosition, boxWidth, surfaceMaterial, placeholder, deductionInfo
) value (
    #{projectId}, #{area}, #{motorModel}, #{trackType}, #{installMethod}, #{number},
    #{openMethod}, #{powerPosition}, #{boxWidth}, #{surfaceMaterial}, #{placeholder}, #{deductionInfo}
)
