mapper.insert:
insert into openMeasure (
    projectId, projectName, area, motorModel, trackType, installMethod, number, type,
    openMethod, powerPosition, boxWidth, surfaceMaterial, placeholder, deductionInfo
) value (
    #{projectId}, #{projectName}, #{area}, #{motorModel}, #{trackType}, #{installMethod}, #{number}, '开合帘',
    #{openMethod}, #{powerPosition}, #{boxWidth}, #{surfaceMaterial}, #{placeholder}, #{deductionInfo}
)
