mapper.select:
# 项目查询
SELECT * FROM projectview p where p.id in (
select id from projectview group by projectId having count(projectId)
)

    and ( projectName like '%${projectName}%' or telephone like '%${projectName}%' or address like '%${projectName}%' )


    and projectId = #{projectId}


    and techId = #{techId}


    and schedule != '调试完结'
