mapper.insert:
# 方案新增
insert into scheme (projectId, productId, number, installNumber,
                    debugNumber, notInstalled, unregulated, createName, createTime, itemId) value (
       #{projectId}, #{productId}, #{number}, #{installNumber}, #{debugNumber},
       #{notInstalled}, #{unregulated}, '初始化', NOW(), #{itemId}
    )

# 方案新增
insert into schemehistory (projectId, productId, number, installNumber,
                           debugNumber, notInstalled, unregulated, createName, createTime, itemId) value (
      #{projectId}, #{productId}, #{number}, #{installNumber},  #{debugNumber},
      #{notInstalled}, #{unregulated}, '初始化', NOW(), #{itemId}
    )
