mapper.select:
# 方案ID查询
select *
from scheme w where id = #{id};

# 方案ID查询
select *
from scheme w where itemId = #{itemId};

mapper.insert:
# 方案新增
insert into scheme (projectId, productId, number, installNumber,
    debugNumber, notInstalled, unregulated, notSent, itemId, createName
) value (
       #{projectId}, #{productId}, #{number}, #{installNumber}, #{debugNumber},
       #{notInstalled}, #{unregulated}, #{notSent}, #{itemId}, #{createName}
    )

        # 方案修改
        update scheme
        set number = #{number},
            notInstalled  = #{notInstalled},
            notSent  = #{notSent}
#             notInstalled  = #{notInstalled},
#             unregulated   = #{unregulated}
        where
            itemId = #{itemId}
