mapper.insert:
# 详情
update worksheet set workSummary = #{workSummary}, visitNode = #{visitNode}
, updateTime = NOW() , updateName = #{updateName}
where id = #{id}

# 签到
update worksheet set signTime = NOW(), updateTime = NOW(), status = '待核销'
where id = #{id}

# 离开
update worksheet set departureTime = NOW(), updateTime = NOW(), status = '待核销'
where id = #{id}

# 核销
update worksheet set writeTime = NOW(), updateTime = NOW(), status = '已完成'
where id = #{id}
