mapper.select:
# 工单查询 select * from workView w where 1 = 1
and techIds like '%${techIds}%'
and projectId like '%${projectId}%'
and workId = #{workId}
and type = #{type}
and projectName like '%${projectName}%' and telephone like '%${projectName}%' and address like '%${projectName}%'
and date_format(`dateOfVisit`,'%Y-%m-%d') = str_to_date(#{dateOfVisit},'%Y-%m-%d')
or headId = #{headId}
order by dateOfVisit asc
