mapper.select:
select *
from rollerMeasure
where disabled is null

    and id = #{id}


    and techId = #{techId}

order by createTime desc

mapper.insert:
insert into rollerMeasure (
    projectId, techId, projectName, area, rollingScheme, length, high, surfaceWidth, type,
    installMethod, deductionInfo, number, surfaceMaterial,
    powerPosition, cover, imgUrl, remark, createName, createTime, itemId
) value (
    #{projectId}, #{techId}, #{projectName}, #{area}, #{rollingScheme}, #{length},
    #{high}, #{surfaceWidth}, '卷帘',
    #{installMethod}, #{deductionInfo}, #{number}, #{surfaceMaterial},
    #{powerPosition}, #{cover}, #{imgUrl}, #{remark}, #{createName}, NOW(), #{itemId}
)
